document.addEventListener("DOMContentLoaded",()=>{const e=864e5,n=e*30,s=e*365,t=(e,t)=>e===1?`1 ${t}`:`${e} ${t}s`;document.querySelectorAll(".time-ago").forEach(o=>{const r=Number(o.dataset.timestamp)*1e3;if(!r)return;const a=Date.now()-r;let i;if(a<0){const o=Math.abs(a);o<e?i="Today":o<n?i=`In ${t(Math.ceil(o/e),"day")}`:o<s?i=`In ${t(Math.ceil(o/n),"month")}`:i=`In ${t(Math.ceil(o/s),"year")}`}else a<e?i="Today":a<n?i=`${t(Math.floor(a/e),"day")} ago`:a<s?i=`${t(Math.floor(a/n),"month")} ago`:i=`${t(Math.floor(a/s),"year")} ago`;o.textContent=i})})